  const[counter, setValue] = useState(0);
  const onClick = ()=>((prev) => prev + 1);
  return (
    <div className="App">
      <h1 className={styled.title}>welcome back!</h1>
      <Button onClick = {onClick} text="Button" />
    </div>
  );
}

export default App;
